import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FlightService } from './../api/services/flight.service';
import { BookDto, FlightRm } from '../api/models';
import { AuthService } from '../auth/auth.service';
import { FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-book-flight',
  templateUrl: './book-flight.component.html',
  styleUrls: ['./book-flight.component.css']
})

export class BookFlightComponent implements OnInit {

  constructor(private route: ActivatedRoute,
    private router: Router,
    private flightService: FlightService,
    private authService: AuthService,
    private formBuilder: FormBuilder  ) { }


  flightId: string = 'not loaded'
  flight: FlightRm = {}

  //forma bookiranja
  form = this.formBuilder.group({
    //Definisanje polja sa validacijom
    number: [1, Validators.compose([Validators.required, Validators.min(1), Validators.max(254)])]
  })


  ngOnInit(): void {

    if (!this.authService.currentUser)
      this.router.navigate(['/register-passenger'])

    this.route.paramMap
      .subscribe(p => this.findFlight(p.get("flightId")))
  }

  private findFlight = (flightId: string | null) => {
    this.flightId = flightId ?? 'not passed';

    this.flightService.findFlight({ id: this.flightId })
      .subscribe(flight => this.flight = flight,
        this.handleError)
  }

  private handleError = (err: any) => {

    if (err.status == 404) {
      alert("Flight not found!")
      this.router.navigate(['/search-flights'])
    }

    if (err.status == 409) {
      console.log("err: " + err)
      alert(JSON.parse(err.error).message)
    }

    console.log("Response Error. Status: ", err.status)
    console.log("Response Error. Status Text: ", err.statusText)
    console.log(err)
  }

  //metoda forme za Booking
  book() {

    if (this.form.invalid)
      return;

    console.log(`Booking ${this.form.get('number')?.value} passengers for the flight: ${this.flight.id}`)
    const numberOfSeats = this.form.get('number')?.value;

    const booking: BookDto = {
      flightId: this.flight.id,
      passengerEmail: this.authService.currentUser?.email,
      numberOfSeats: numberOfSeats !== null ? numberOfSeats : undefined
    }

    //dodali smo putanju da Componente My-Booking, povezujemo 
    this.flightService.bookFlight({ body: booking })
      .subscribe(_ => this.router.navigate(['/my-bookings']), this.handleError)
  }

  get number() {
    return this.form.controls.number
  }


}
<h5 class="text-md-center m-5">Book Flight</h5>

<div class="card-body text-center" style="padding: 20px; background-color:#e5ecf26e">
  <h6 class="card-title ">{{flight.airline}}</h6>
  <div class="card-title" style="color: #ff3939">{{flight.remainingNumberOfSeats}} remaining seats</div>
  <div class="d-grid text-center g-2">
    <div class="row">
      <div class="col">
        <div class="col m-3">

          <i class="text-primary fas fa-plane-departure" style="font-size: 20px;"></i>
        </div>
        <div class="col">
          <i class="fa-solid fa-location-dot">
            {{flight.departure?.place}}
          </i>

        </div>
        <div class="col m-2">
          <i class="fa-regular"></i>
          {{flight.departure?.time | date}}

        </div>
        <div class="col">

          <i class="fa-regular fa-clock">
            {{flight.departure?.time | date:'shortTime'}}
          </i>
        </div>
      </div>

      <div class="col">
        <div style="display: flex;">
          <i class="fa-solid  fa-circle" style="margin-top:51px; font-size: 9px;"></i>
          <hr style="display:block;width: 100%;margin-top: 55px;">
          <i class="fas fa-plane" style="margin-top: 48px;"></i>


        </div>
        <i class="fa fa-usd" aria-hidden="true">{{flight.price}}</i>
        <br>

      </div>
      <div class="col">
        <div class="col m-3">
          <i class="text-primary fas fa-plane-arrival" style="font-size: 20px;"></i>

        </div>
        <div class="col">

          <i class="fa-solid fa-location-dot">
            {{flight.arrival?.place}}
          </i>
        </div>
        <div class="col m-2 text-primary">
          <i class="fa-regular "></i>
          {{flight.arrival?.time | date}}

        </div>
        <div class="col">
          <i class="fa-regular fa-clock">
            {{flight.arrival?.time | date:'shortTime'}}
          </i>

        </div>
      </div>
    </div>
  </div>
</div>

<div class="card-footer d-flex justify-content-center">

  <form [formGroup]="form" (ngSubmit)="book()">
    <label class="form-label mt-5" for="numberOfSeats">Number of Seats</label>
    <div class="center input-group">

      <input formControlName="number" type="number" class="form-control" id="numberOfSeats" name="numberOfSeats"
             placeholder="Number of Seats" />
      <button type="submit" class="btn btn-primary">
        <i class="fas fa-ticket"></i>
        Book
      </button>
    </div>
  </form>

</div>

<div *ngIf="number.invalid && (number.dirty || number.touched)" class="invalid-feedback d-block">
  <div *ngIf="number.errors?.['required']">
    How many people do you want to book the flight for?
  </div>
  <div *ngIf="number.errors?.['min']">
    You can not book for less than 1 passenger!
  </div>
  <div *ngIf="number.errors?.['max']">
    Our planes do not have more than 254 seats.
  </div>
</div>
