import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']



})
export class AppComponent {
  title = 'app';
}


//styleUrls: Ovaj atribut je deo metadata koji se koristi unutar definicije Angular komponente.
//On omogućuje komponenti da referencira izdvojene CSS datoteke ili stilove.

//['./app.component.css']: Ovde je specificirana relativna putanja do CSS datoteke koja sadrži
//stilove za komponentu.U ovom slučaju,./ app.component.css označava da se CSS stilovi
//za komponentu nalaze u datoteci nazvanoj app.component.css, koja se nalazi u
//istom direktoriju kao i komponenta.
<body>
  <app-nav-menu></app-nav-menu> 
  <header class="mb-5 header"></header> <!--Heder-->
  <div class="container">
    <router-outlet></router-outlet>
  </div>
  <!--Futer page-->
  <footer class="pt-3 footer  bg-dark text-light text-center mt-5" style="margin-top: 62px;">

    <div class="pt-5"> <!--klasa pt-5 je atribut koji dodeljuje CSS klasu elementu.Klasa pt-5
    označava da je ovaj div elemenat definiran s top paddingom od 5 piksela-->
      <a href="/"><i class="ms-3 fa-lg fab fa-linkedin-in float-lg"></i></a>
      <a href="/"><i class="ms-3 fa-lg fab fa-twitter"></i></a>
      <a href="/"><i class="ms-3 fa-lg fab fa-facebook"></i></a>
      <a href="/"><i class="ms-3 fa-lg fab fa-google-plus"></i></a>
      <a href="/"><i class="ms-3 fa-lg fab fa-instagram"></i></a>
      <a href="/"><i class="ms-3 fa-lg fab fa-github"></i></a>
    </div>

    <div class="mt-3">
      <i class="fas ">TutorialsEU </i>
    </div>

    <div class=" text-muted">
      <i>© 2022 Copyright: TutorialsEU </i>
    </div>

  </footer>
</body>
